import java.util.*;public class Rec8{	public static void main(String [] args)	{		Scanner S = new Scanner(System.in);		System.out.print("Array size > ");		int size = S.nextInt();  S.nextLine();		Integer [] A = new Integer[size];		Integer [] B = new Integer[size];		Random R = new Random();		for (int i = 0; i < A.length; i++)		{			int val = R.nextInt();			Integer X = Integer.valueOf(val);			Integer Y = Integer.valueOf(val);			A[i] = X;			B[i] = Y;		}		long start = System.nanoTime();		TextMerge.mergeSort(A, A.length);		long stop = System.nanoTime();				if (A.length <= 50)		{			for (int i = 0; i < A.length; i++)				System.out.print(A[i] + " ");			System.out.println();		}						long diff = stop - start;		double sec = (double) diff / 1000000000;		System.out.println("MergeSort took " + sec + " sec");				start = System.nanoTime();		TextMerge.mergeSortB(B, B.length);		stop = System.nanoTime();				if (B.length <= 50)		{			for (int i = 0; i < B.length; i++)				System.out.print(B[i] + " ");			System.out.println();		}						diff = stop - start;		sec = (double) diff / 1000000000;		System.out.println("MergeSort version B took " + sec + " sec");	}}